    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
            // TODO add your handling code here:
        int checkProductExist = 0;
        String uniqueId = txtProductId.getText();
        
        if(uniqueId.equals("")){
            JOptionPane.showMessageDialog(null, "¡Debes ingresar el ID del producto!","Error",JOptionPane.ERROR_MESSAGE);
        } else {
            try{
                Connection con = ConnectionProvider.getCon();
                Statement st = con.createStatement();
                ResultSet rs = st.executeQuery("select * from products where uniqueId="+uniqueId+"");
                while(rs.next()){
                    txtProductId.setEditable(false);
                    txtName.setText(rs.getString("name"));
                    txtCompanyName.setText(rs.getString("companyName"));
                    txtQuantity.setText(rs.getString("quantity"));
                    txtPricePerUnit.setText(rs.getString("price"));
                    //txtQuantity.setEditable(false);  PARA QUE NO SE PUEDA EDITAR EL CAMPO DE CANTIDAD DEL PRODUCTO
                    txtQuantity.setEditable(true);
                    checkProductExist = 1;
                }
            }
            catch(Exception e){
                JOptionPane.showMessageDialog(null, e);
            }
            
            if(checkProductExist == 0){
                JOptionPane.showMessageDialog(null, "¡El ID de producto ingresado no existe!","Error",JOptionPane.ERROR_MESSAGE);
            }
        }
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        String uniqueId = txtProductId.getText();
        String name = txtName.getText();
        String companyName = txtCompanyName.getText();
        String quantity = txtQuantity.getText();
        String price = txtPricePerUnit.getText();
        String addQuantity = txtAddQuantity.getText(); // CAMPO EN EL QUE SE INGRESA LA CANTIDAD DE PRODUCTOS A AÑADIR
        
        // SE SUMA LA CANTIDAD INGRESADA CON LA YA EXISTENTE
        int totalQuantity = 0;
        if(addQuantity.equals("")){
            totalQuantity = Integer.parseInt(quantity);
        }else {
            totalQuantity = Integer.parseInt(quantity) + Integer.parseInt(addQuantity);
        }
        
        if(uniqueId.equals("")){
            JOptionPane.showMessageDialog(null, "¡Debes ingresar el ID del producto!");
        }else if (name.equals("")){
            JOptionPane.showMessageDialog(null, "¡Debes ingresar el nombre del producto!");
        }else if (companyName.equals("")){
            JOptionPane.showMessageDialog(null, "¡Debes ingresar la marca del producto!");
        }else if(!addQuantity.matches(numberPattern)){
            JOptionPane.showMessageDialog(null, "¡Debes ingresar la cantidad del producto en números!");
        } else if(price.equals("")){
            JOptionPane.showMessageDialog(null, "¡Debes ingresar el precio del producto!");
        }else if (!price.matches(numberPattern)){
            JOptionPane.showMessageDialog(null, "¡Debes ingresar el precio del producto en números!");
        }else {
            try{
                Connection con = ConnectionProvider.getCon();
                PreparedStatement ps = con.prepareStatement("update products set name=?, companyName=?, quantity=?, price=? where uniqueId=?");
                ps.setString(1, name);
                ps.setString(2, companyName);
                //ps.setInt(3, totalQuantity); SE AGREGA A LA BASE DE DATOS LA CANTIDAD TOTAL SI SE HA INGRESADO
                ps.setString(3, quantity);
                ps.setString(4, price);
                ps.setInt(5, Integer.parseInt(uniqueId));
                ps.executeUpdate();
                JOptionPane.showMessageDialog(null, "¡Producto actualizado exitosamente!");
                setVisible(false);
                new UpdateProduct().setVisible(true);
            }
            catch(Exception e){
                JOptionPane.showMessageDialog(null, e);
            }
        }
    }//GEN-LAST:event_jButton3ActionPerformed

